import React, { useEffect, useState } from "react";
import axios from "axios";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.min.css";
import SwiperCore, { Navigation, Pagination } from "swiper";

SwiperCore.use([Navigation, Pagination]);

function Home() {
  const [novidades, setNovidades] = useState([]);
  const [maisVendidos, setMaisVendidos] = useState([]);

  useEffect(() => {
    // Fetch para Novidades
    axios
      .get("http://localhost:5000/produto")
      .then((response) => setNovidades(response.data))
      .catch((error) => console.error("Erro ao buscar novidades:", error));

    // Fetch para Mais Vendidos
    axios
      .get("http://localhost:5000/produto?tipo=vendidos")
      .then((response) => setMaisVendidos(response.data))
      .catch((error) => console.error("Erro ao buscar mais vendidos:", error));
  }, []);

  const handleAdicionarCarrinho = (produto) => {
    const carrinho = JSON.parse(localStorage.getItem("carrinho")) || [];
    const produtoExistente = carrinho.find((item) => item.IDproduto === produto.IDproduto);

    if (produtoExistente) {
      alert("Produto já está no carrinho!");
    } else {
      carrinho.push(produto);
      localStorage.setItem("carrinho", JSON.stringify(carrinho));
      alert("Produto adicionado ao carrinho!");
    }
  };

  const renderProduto = (produto) => (
    <SwiperSlide key={produto.IDproduto}>
      <div className="swiper-slide">
        <a
          className="carrossel__imagens"
          href={`produtos.html?id=${produto.IDproduto}`}
        >
          <img
            className="img_produto"
            src={produto.Imagens}
            alt={produto.Nome}
          />
        </a>
        <div className="infos__produto">
          <a
            className="titulo__produto color"
            href={`produtos.html?id=${produto.IDproduto}`}
          >
            {produto.Nome}
          </a>
          <p className="descricao__produto">{produto.Descricao}</p>
          <p className="valor__produto color">R${produto.Preco.toFixed(2)}</p>
          <button
            className="adicionar__carrinho"
            onClick={() => handleAdicionarCarrinho(produto)}
          >
            Adicionar ao Carrinho
          </button>
        </div>
      </div>
    </SwiperSlide>
  );

  return (
    <div>
      <main className="principal">
        <section className="principal__novo_produto">
          <h2 className="principal__novo_produto__titulo">
            Descubra o novo tesouro da nossa coleção: um artesanato feito com amor
            e cuidado, trazendo frescor e beleza para sua vida.
          </h2>
          <p className="principal__novo_produto__p">
            Ótimo presente para você e seus entes queridos.
          </p>
          <a href="todos_produtos.html" className="principal__novo_produto__link">
            Compre Agora
          </a>
        </section>

        {/* Novidades */}
        <section className="background__branco carrossel__full">
          <h2 className="principal__novidades__titulo">
            <img src="assets/images/global/flor1.svg" />
            <b className="titulo__novidades">Novidades</b>
            <img src="assets/images/global/flor2.svg" />
          </h2>
          <Swiper
            spaceBetween={25}
            slidesPerView={window.innerWidth <= 1024 ? 2 : 3}
            centeredSlides
            loop
            navigation
            pagination={{ clickable: true }}
          >
            {novidades.map(renderProduto)}
          </Swiper>
        </section>

        {/* Mais Vendidos */}
        <section className="background__branco carrossel__full">
          <h2 className="principal__novidades__titulo">
            <img src="assets/images/global/flor1.svg" />
            <b className="titulo__novidades">Mais Vendidos</b>
            <img src="assets/images/global/flor2.svg" />
          </h2>
          <Swiper
            spaceBetween={25}
            slidesPerView={window.innerWidth <= 1024 ? 2 : 3}
            centeredSlides
            loop
            navigation
            pagination={{ clickable: true }}
          >
            {maisVendidos.map(renderProduto)}
          </Swiper>
        </section>

        {/* Produto Destaque */}
        <section className="background__branco">
          <section className="produto__destaque">
            <div className="produto__destaque__flex">
              <img
                className="produto__destaque__foto"
                src="assets/images/pages/index/foto_produto_destaque.png"
                alt="Foto do produto em destaque"
              />
            </div>
            <div className="bloco__descricao_botao">
              <h2 className="produto__destaque__titulo">Nome do Produto</h2>
              <p className="produto__destaque__descricao">
                Descrição do Produto: Lorem ipsum dolor sit amet consectetur
                adipisicing elit.
              </p>
              <a className="produto__destaque__botao" href="produtos.html">
                Ver Produto
              </a>
            </div>
          </section>
        </section>

        {/* Compartilhe */}
        <section className="background__branco">
          <section className="compartilhar">
            <h2 className="compartilhar__titulo">Compartilhe com a Gente</h2>
            <h2 className="compartilhar__subtitulo">#Fram<b>artesanatos</b></h2>
            <div className="compartilhar__fotos">
              {[...Array(6)].map((_, i) => (
                <img
                  key={i}
                  className="compartilhar__imagens"
                  src="assets/images/pages/index/instagram (6).svg"
                  alt="Instagram"
                />
              ))}
              <a
                className="compartilhar__botao"
                href="https://www.instagram.com/"
                target="_blank"
              >
                Siga-nos
              </a>
            </div>
          </section>
        </section>
      </main>
    </div>
  );
}

export default Home;
