import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import { Link, useNavigate } from 'react-router-dom';  // useNavigate para navegação programática
import { useParams } from 'react-router-dom';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

function Produto() {
  const { id } = useParams();
  const [produto, setProduto] = useState(null); // Para o produto específico
  const [produtosCarrossel, setProdutosCarrossel] = useState([]); // Para os produtos do carrossel
  const navigate = useNavigate(); // Usado para navegação programática

  useEffect(() => {
    // Carregar o produto específico
    if (id) {
      axios.get(`http://localhost:5000/produto/${id}`)
        .then(response => setProduto(response.data)) // Produto específico
        .catch(error => console.error('Erro ao buscar produto:', error));
    }

    // Carregar todos os produtos para o carrossel
    axios.get(`http://localhost:5000/produto`)
      .then(response => setProdutosCarrossel(response.data)) // Todos os produtos para o carrossel
      .catch(error => console.error('Erro ao buscar produtos para o carrossel:', error));

  }, [id]);

  if (!produto || produtosCarrossel.length === 0) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="produto">
          <div className="box__produto">
            <div className="center__foto">
              <img className="foto__produto__principal" src={produto.Imagens} alt={produto.Nome} />
            </div>
            <div>
              <h2 className="nome__produto__principal">{produto.Nome}</h2>
              <h3 className="valor__produto__principal">R${produto.Preco.toFixed(2)}</h3>
              <p className="descricao__produto__principal">{produto.Descricao}</p>
              <button className="adicionar__carrinho__produto">
                Adicionar ao Carrinho
              </button>
            </div>
          </div>
        </section>

        <section className="background__branco carrossel__full">
          <section className="principal__novidades mais__vendidos">
            <h2 className="principal__novidades__titulo">
              <img src="assets/images/global/flor1.svg" alt="flor" />
              <b className="titulo__novidades">Mais Vendidos</b>
              <img src="assets/images/global/flor2.svg" alt="flor" />
            </h2>
            <p className="principal__novidades__subtitulo">Veja Tudo</p>

            <article className="principal__novidades__carrossel">
              <div className="carrossel__container">
                <div className="swiper mais__vendidos">
                  <div className="swiper-button-prev mais__vendidos">&lt;</div>
                  <div className="swiper-button-next mais__vendidos">&gt;</div>
                  <Swiper
                    modules={[Navigation, Pagination]}
                    spaceBetween={25}
                    slidesPerView={window.matchMedia("(max-width: 1024px)").matches ? 2 : 3}
                    centeredSlides={true}
                    loop={true}
                    pagination={{ clickable: true }}
                    navigation={{
                      prevEl: '.swiper-button-prev.mais__vendidos',
                      nextEl: '.swiper-button-next.mais__vendidos',
                    }}
                  >
                    {produto.map((produto) => (
                      <SwiperSlide key={produto.IDproduto} className="swiper-slide">
                        <Link
                          className="carrossel__imagens"
                          to={`/produto/${produto.IDproduto}`}
                        >
                          <img
                            className="img_produto"
                            src={produto.Imagens}
                            alt={`Foto do Produto ${produto.Nome}`}
                          />
                        </Link>
                        <div className="infos__produto">
                          <Link
                            className="titulo__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Nome}
                          </Link>
                          <Link
                            className="descricao__produto"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Descricao}
                          </Link>
                          <Link
                            className="valor__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            R${produto.Preco.toFixed(2)}
                          </Link>
                          <button
                            className="adicionar__carrinho"
                            data-produto={JSON.stringify({
                              IDproduto: produto.IDproduto,
                              Nome: produto.Nome,
                              Descricao: produto.Descricao,
                              Preco: produto.Preco,
                              Imagens: produto.Imagens,
                            })}
                          >
                            Adicionar ao Carrinho
                          </button>
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>
            </article>
          </section>
        </section>
      </main>
    </div>
  );
}

export default Produto;
