import React from "react";


class MobileNavbar {
  constructor(menuSelector, navSelector) {
    this.mobileMenu = document.querySelector(menuSelector);
    this.navList = document.querySelector(navSelector);
    this.activeClass = "active";
    this.hamburger = document.querySelector(".cabecalho__nav__menu_hamburguer");
    this.nav = document.querySelector(".cabecalho__nav");

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.toggleActiveClass(this.mobileMenu);
    this.toggleActiveClass(this.nav);
  }

  toggleActiveClass(element) {
    element.classList.toggle(this.activeClass);
  }

  addClickEvent() {
    this.hamburger.addEventListener("click", this.handleClick);
  }

  init() {
    if (this.mobileMenu && this.hamburger && this.nav) {
      this.addClickEvent();
    }
    return this;
  }
}

const mobileNavbar = new MobileNavbar(".mobile-menu", ".nav-lista");
mobileNavbar.init();

const Header = () => {
  return (
    <header className="cabecalho">
      <nav className="cabecalho__nav">
        <button className="cabecalho__nav__menu_hamburguer">
          <div className="mobile-menu">
            <div className="line1"></div>
            <div className="line2"></div>
            <div className="line3"></div>
          </div>
        </button>

        <ul className="nav-lista">
          <h2 className="nav-lista__titulo">Menu</h2>

          <li className="nav-lista__item">
            <ul className="itens__menu__hamburguer">
              <li className="nav-lista__item">
                <a href="todos_produtos.html">
                  <p className="font">Todos os Produtos</p>
                </a>
              </li>

              <li className="nav-lista__item margin__baixo">
                <a href="sobre_nos.html">
                  <p className="font">Sobre NÃ³s</p>
                </a>
              </li>
            </ul>
          </li>
        </ul>

        <h1 className="cabecalho__nav__titulo">
          <a href="index.html">
            <b className="titulo_destaque">Fram</b> Artesanatos
          </a>
        </h1>

        <section className="botao__carrinho">
          <a href="carrinho.html" className="botoes__carrinho">
            <img
              className="cabecalho__nav__carrinho_img"
              src="/assets/images/global/icon_carrinho.svg"
              alt="Carrinho de compras"
            />
          </a>
        </section>
      </nav>
    </header>
  );
};

export default Header;
