import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import { Link } from 'react-router-dom';
import { CarrinhoContext } from '../context/CarrinhoContext.js';
import Popup from '../components/Popup.js';

import '../css/import.css';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

function Home() {
  const [produtos, setProdutos] = useState([]);
  const [popupMessage, setPopupMessage] = useState(null);
  const { adicionarAoCarrinho, produtoJaNoCarrinho } = useContext(CarrinhoContext);

  const limiteNovidades = 1;
  const limiteMaisVendidos = 1;

  useEffect(() => {
    axios
      .get("http://localhost:5000/produto")
      .then((response) => {
        const produtosEmbaralhados = response.data.sort(() => 0.5 - Math.random()); 
        setProdutos(produtosEmbaralhados);
      })
      .catch((error) => console.error("Erro ao buscar produtos:", error));
  }, []);

  const handleAdicionarAoCarrinho = (produto) => {
    if (produtoJaNoCarrinho(produto.IDproduto)) {
      setPopupMessage('jaAdicionado');
    } else {
      adicionarAoCarrinho(produto);
      setPopupMessage('adicionado');
    }

    setTimeout(() => setPopupMessage(null), 6000);
  };

  const produtosNovidades = produtos.slice(0, limiteNovidades);
  const produtosMaisVendidos = produtos.slice(0, limiteMaisVendidos);

  return (
    <div>
      <main className="principal">
        <section className="principal__novo_produto">
          <h2 className="principal__novo_produto__titulo">
            Descubra o novo tesouro da nossa coleção: um artesanato feito com amor
            e cuidado, trazendo frescor e beleza para sua vida.
          </h2>
          <p className="principal__novo_produto__p">
            Ótimo presente para você e seus entes queridos.
          </p>
          <Link to="/todosProdutos" className="principal__novo_produto__link">
            Compre Agora
          </Link>
        </section>

        <section className="background__branco carrossel__full">
          <section className="principal__novidades">
            <h2 className="principal__novidades__titulo">
              <img src="images/global/flor1.svg" alt="flor" />
              <b className="titulo__novidades">Novidades</b>
              <img src="images/global/flor2.svg" alt="flor" />
            </h2>
            <p className="principal__novidades__subtitulo">Veja Tudo</p>

            <article className="principal__novidades__carrossel">
              <div className="carrossel__container">
                <div className="swiper novidades">
                  <div className="swiper-button-prev novidades"></div>
                  <div className="swiper-button-next novidades"></div>
                  <Swiper
                    modules={[Navigation, Pagination]}
                    spaceBetween={25}
                    slidesPerView={window.matchMedia('(max-width: 1024px)').matches ? 2 : 3}
                    centeredSlides={true}
                    loop={true}
                    pagination={{ clickable: true }}
                    navigation={{
                      prevEl: '.swiper-button-prev.novidades',
                      nextEl: '.swiper-button-next.novidades',
                    }}
                  >
                    {produtosNovidades.map((produto) => (
                      <SwiperSlide key={produto.IDproduto} className="swiper-slide">
                        <Link
                          className="carrossel__imagens"
                          to={`/produto/${produto.IDproduto}`}
                        >
                          <img
                            className="img_produto"
                            src={produto.Imagens}
                            alt={`Foto do Produto ${produto.Nome}`}
                          />
                        </Link>
                        <div className="infos__produto">
                          <Link
                            className="titulo__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Nome}
                          </Link>
                          <Link
                            className="descricao__produto"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Descricao}
                          </Link>
                          <Link
                            className="valor__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            R${produto.Preco.toFixed(2)}
                          </Link>
                          <button
                            className="adicionar__carrinho"
                            onClick={() => handleAdicionarAoCarrinho(produto)}
                          >
                            Adicionar ao Carrinho
                          </button>
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>
            </article>
          </section>
        </section>

        <section className="background__branco carrossel__full">
          <section className="principal__novidades mais__vendidos">
            <h2 className="principal__novidades__titulo">
              <img src="images/global/flor1.svg" alt="flor" />
              <b className="titulo__novidades">Mais Vendidos</b>
              <img src="images/global/flor2.svg" alt="flor" />
            </h2>
            <p className="principal__novidades__subtitulo">Veja Tudo</p>

            <article className="principal__novidades__carrossel">
              <div className="carrossel__container">
                <div className="swiper mais__vendidos">
                  <div className="swiper-button-prev mais__vendidos"></div>
                  <div className="swiper-button-next mais__vendidos"></div>
                  <Swiper
                    modules={[Navigation, Pagination]}
                    spaceBetween={25}
                    slidesPerView={window.matchMedia("(max-width: 1024px)").matches ? 2 : 3}
                    centeredSlides={true}
                    loop={true}
                    pagination={{ clickable: true }}
                    navigation={{
                      prevEl: '.swiper-button-prev.mais__vendidos',
                      nextEl: '.swiper-button-next.mais__vendidos',
                    }}
                  >
                    {produtosMaisVendidos.map((produto) => (
                      <SwiperSlide key={produto.IDproduto} className="swiper-slide">
                        <Link
                          className="carrossel__imagens"
                          to={`/produto/${produto.IDproduto}`}
                        >
                          <img
                            className="img_produto"
                            src={produto.Imagens}
                            alt={`Foto do Produto ${produto.Nome}`}
                          />
                        </Link>
                        <div className="infos__produto">
                          <Link
                            className="titulo__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Nome}
                          </Link>
                          <Link
                            className="descricao__produto"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            {produto.Descricao}
                          </Link>
                          <Link
                            className="valor__produto color"
                            to={`/produto/${produto.IDproduto}`}
                          >
                            R${produto.Preco.toFixed(2)}
                          </Link>
                          <button
                            className="adicionar__carrinho"
                            onClick={() => handleAdicionarAoCarrinho(produto)}
                          >
                            Adicionar ao Carrinho
                          </button>
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>
            </article>
          </section>
        </section>

        <section className="background__branco">
          <section className="produto__destaque">
            <div className="produto__destaque__flex">
              <img className="produto__destaque__foto" src="images/pages/index/foto_produto_destaque.png"
                alt="Foto do produto em destaque" />
            </div>
            <div className="bloco__descricao_botao">
              <h2 className="produto__destaque__titulo">Nome do Produto</h2>
              <p className="produto__destaque__descricao">
                Descrição do Produto: Lorem ipsum dolor sit amet consectetur
                adipisicing elit. Sapiente maxime quisquam sunt nostrum nobis eum a
                reiciendis officiis exercitationem itaque, nam debitis beatae. Ipsum
                tempora laborum sit maxime ex debitis?
              </p>
              <a className="produto__destaque__botao" href="produtos.html">Ver Produto</a>
            </div>
          </section>
        </section>

        <section className="background__branco">
          <section className="produto__destaque">
            <div className="produto__destaque__flex">
              <img className="produto__destaque__foto foto__destaque2" src="images/pages/index/foto_produto_destaque.png"
                alt="Foto do produto em destaque" />
            </div>
            <div className="bloco__descricao_botao">
              <h2 className="produto__destaque__titulo">Nome do Produto</h2>
              <p className="produto__destaque__descricao">
                Descrição do Produto: Lorem ipsum dolor sit amet consectetur
                adipisicing elit. Sapiente maxime quisquam sunt nostrum nobis eum a
                reiciendis officiis exercitationem itaque, nam debitis beatae. Ipsum
                tempora laborum sit maxime ex debitis?
              </p>
              <a className="produto__destaque__botao" href="produtos.html">Ver Produto</a>
            </div>
          </section>
        </section>

        <section className="background__branco">
          <section className="compartilhar">
            <h2 className="compartilhar__titulo">Compartilhe com a Gente</h2>
            <h2 className="compartilhar__subtitulo">#Fram<b>artesanatos</b></h2>
            <div className="compartilhar__fotos">
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <img className="compartilhar__imagens" src="images/pages/index/instagram (6).svg" alt="Instagram" />
              <a className="compartilhar__botao" href="https://www.instagram.com/" target="_blank">Siga-nos</a>
            </div>
          </section>
        </section>

        <Popup tipo={popupMessage} fechar={() => setPopupMessage(null)} />
      </main>
    </div>
  );
}

export default Home;
