import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; 
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

const Produto = () => {
  const { id } = useParams(); 
  const [produto, setProduto] = useState(null);
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5000/produto/${id}`)
      .then(response => setProduto(response.data))
      .catch(error => console.error('Erro ao buscar o produto:', error));

    // Carregar produtos mais vendidos
    axios.get('http://localhost:5000/produto')
      .then(response => setProdutos(response.data))
      .catch(error => console.error('Erro ao buscar produtos:', error));
  }, [id]); // Dependência no id, para refazer a busca ao mudar o id

  const adicionarAoCarrinho = (produto) => {
    const carrinho = JSON.parse(localStorage.getItem("carrinho")) || [];
    const produtoExistente = carrinho.some(item => item.IDproduto === produto.IDproduto);
    if (produtoExistente) {
      alert('Produto já adicionado ao carrinho');
    } else {
      carrinho.push(produto);
      localStorage.setItem("carrinho", JSON.stringify(carrinho));
      alert('Produto adicionado ao carrinho');
    }
  };

  if (!produto) return <div>Carregando...</div>; // Espera o produto ser carregado

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="produto">
          <div className="box__produto">
            <div className="center__foto">
              <img className="foto__produto__principal" src={produto.Imagens} alt={`Foto do produto ${produto.Nome}`} />
            </div>
            <div>
              <h2 className="nome__produto__principal">{produto.Nome}</h2>
              <h3 className="valor__produto__principal">R${produto.Preco.toFixed(2)}</h3>
              <p className="descricao__produto__principal">{produto.Descricao}</p>
              <button
                className="adicionar__carrinho__produto"
                onClick={() => adicionarAoCarrinho(produto)}
              >
                Adicionar ao Carrinho
              </button>
            </div>
          </div>
        </section>

        <section className="background__branco carrossel__full">
          <section className="principal__novidades mais__vendidos">
            <h2 className="principal__novidades__titulo">
              <img src="assets/images/global/flor1.svg" alt="flor" />
              <b className="titulo__novidades">Mais Vendidos</b>
              <img src="assets/images/global/flor2.svg" alt="flor" />
            </h2>
            <p className="principal__novidades__subtitulo">Veja Tudo</p>

            <article className="principal__novidades__carrossel">
              <div className="carrossel__container">
                <div className="swiper mais__vendidos">
                  <div className="swiper-button-prev mais__vendidos">&lt;</div>
                  <div className="swiper-button-next mais__vendidos">&gt;</div>
                  <Swiper
                    modules={[Navigation, Pagination]}
                    spaceBetween={25}
                    slidesPerView={window.matchMedia("(max-width: 1024px)").matches ? 2 : 3}
                    centeredSlides={true}
                    loop={true}
                    pagination={{ clickable: true }}
                    navigation={{
                      prevEl: '.swiper-button-prev.mais__vendidos',
                      nextEl: '.swiper-button-next.mais__vendidos',
                    }}
                  >
                    {produtos.map((produto) => (
                      <SwiperSlide key={produto.IDproduto} className="swiper-slide">
                        <a className="carrossel__imagens" href={`/produto/${produto.IDproduto}`}>
                          <img className="img_produto" src={produto.Imagens} alt={`Foto do Produto ${produto.Nome}`} />
                        </a>
                        <div className="infos__produto">
                          <a className="titulo__produto color" href={`/produto/${produto.IDproduto}`}>
                            {produto.Nome}
                          </a>
                          <a className="descricao__produto" href={`/produto/${produto.IDproduto}`}>
                            {produto.Descricao}
                          </a>
                          <a className="valor__produto color" href={`/produto/${produto.IDproduto}`}>
                            R${produto.Preco.toFixed(2)}
                          </a>
                          <button
                            className="adicionar__carrinho"
                            onClick={() => adicionarAoCarrinho(produto)}
                          >
                            Adicionar ao Carrinho
                          </button>
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>
            </article>
          </section>
        </section>
      </main>
    </div>
  );
};

export default Produto;
