import React from 'react';
import { BrowserRouter as Roteador, Route, Routes } from 'react-router-dom';
import Cabecalho from './componentes/Cabecalho';
import Rodape from './componentes/Rodape';
import { CarrinhoProvider } from './context/CarrinhoContext';
import ScrollParaTopo from './componentes/ScrollParaTopo';

import Home from './paginas/Home';
import Produto from './paginas/Produto';
import SobreNos from './paginas/SobreNos';
import Carrinho from './paginas/Carrinho';
import TodosProdutos from './paginas/TodosProdutos';

function App() {
  return (
    <CarrinhoProvider>
      <Roteador>
        <ScrollParaTopo />
        <Cabecalho />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/produto/:id/:nome" element={<Produto />} />
          <Route path="/sobre-nos" element={<SobreNos />} />
          <Route path="/carrinho" element={<Carrinho />} />
          <Route path="/todosProdutos" element={<TodosProdutos />} />
        </Routes>
        <Rodape />
      </Roteador>
    </CarrinhoProvider>
  );
}

export default App;
