import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { CarrinhoContext } from '../context/CarrinhoContext.js';

const TodosProdutos = () => {
  const [produtos, setProdutos] = useState([]);
  const [popupMessage, setPopupMessage] = useState(null); // Controla o tipo de pop-up
  const { adicionarAoCarrinho, produtoJaNoCarrinho } = useContext(CarrinhoContext);

  useEffect(() => {
    axios
      .get('http://localhost:5000/produto')
      .then((response) => setProdutos(response.data))
      .catch((error) => console.error('Erro ao buscar os produtos:', error));
  }, []);

  const handleAdicionarAoCarrinho = (produto) => {
    if (produtoJaNoCarrinho(produto.IDproduto)) {
      setPopupMessage('jaAdicionado');
    } else {
      adicionarAoCarrinho(produto);
      setPopupMessage('adicionado');
    }

    // Fechar o pop-up automaticamente após 3 segundos
    setTimeout(() => setPopupMessage(null), 3000);
  };

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="produtos">
          {produtos.map((produto) => (
            <div key={produto.IDproduto} className="produtos__1">
              <Link to={`/produto/${produto.IDproduto}`} className="produto__link">
                <img className="produto__img" src={produto.Imagens} alt={produto.Nome} />
                <div className="produto__info">
                  <h3 className="produto__titulo">{produto.Nome}</h3>
                  <p className="produto__descricao">{produto.Descricao}</p>
                  <p className="produto__valor">R${produto.Preco.toFixed(2)}</p>
                </div>
              </Link>
              <button
                className="adicionar__carrinho"
                onClick={() =>
                  handleAdicionarAoCarrinho({
                    IDproduto: produto.IDproduto,
                    Nome: produto.Nome,
                    Descricao: produto.Descricao,
                    Preco: produto.Preco,
                    Imagens: produto.Imagens,
                  })
                }
              >
                Adicionar ao Carrinho
              </button>
            </div>
          ))}
        </section>
      </main>

      {/* Pop-up para produto adicionado */}
      {popupMessage === 'adicionado' && (
        <div id="popup_carrinho" className="popup">
          <div className="popup-content">
            <h3>Produto Adicionado ao Carrinho!</h3>
            <p>Seu produto foi adicionado com sucesso.</p>
            <div className="botoes__popup">
              <button onClick={() => setPopupMessage(null)}>Voltar às Compras</button>
              <Link to="/carrinho">Ver Carrinho</Link>
            </div>
          </div>
        </div>
      )}

      {/* Pop-up para produto já adicionado */}
      {popupMessage === 'jaAdicionado' && (
        <div id="popup_jaadd" className="popup">
          <div className="popup-content">
            <h3>Produto Já Adicionado ao Carrinho!</h3>
            <p>Este produto já está no seu carrinho.</p>
            <div className="botoes__popup">
              <button onClick={() => setPopupMessage(null)}>Voltar às Compras</button>
              <Link to="/carrinho">Ver Carrinho</Link>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TodosProdutos;
