import React, { useEffect, useState } from "react";
import axios from "axios";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/swiper-bundle.min.css";
import SwiperCore, { Navigation, Pagination } from "swiper";

SwiperCore.use([Navigation, Pagination]);

function Home() {
  const [novidades, setNovidades] = useState([]);
  const [maisVendidos, setMaisVendidos] = useState([]);

  useEffect(() => {
    // Fetch produtos de Novidades
    axios
      .get("http://localhost:5000/produto")
      .then((response) => setNovidades(response.data))
      .catch((error) => console.error("Erro ao buscar novidades:", error));

    // Fetch produtos de Mais Vendidos
    axios
      .get("http://localhost:5000/produto?tipo=vendidos")
      .then((response) => setMaisVendidos(response.data))
      .catch((error) => console.error("Erro ao buscar mais vendidos:", error));
  }, []);

  const handleAdicionarCarrinho = (produto) => {
    const carrinho = JSON.parse(localStorage.getItem("carrinho")) || [];
    const produtoExistente = carrinho.find((item) => item.IDproduto === produto.IDproduto);

    if (produtoExistente) {
      alert("Produto j치 est치 no carrinho!");
    } else {
      carrinho.push(produto);
      localStorage.setItem("carrinho", JSON.stringify(carrinho));
      alert("Produto adicionado ao carrinho!");
    }
  };

  const renderProduto = (produto) => (
    <SwiperSlide key={produto.IDproduto}>
      <div className="swiper-slide">
        <a
          className="carrossel__imagens"
          href={`produtos.html?id=${produto.IDproduto}`}
        >
          <img
            className="img_produto"
            src={produto.Imagens}
            alt={produto.Nome}
          />
        </a>
        <div className="infos__produto">
          <a
            className="titulo__produto color"
            href={`produtos.html?id=${produto.IDproduto}`}
          >
            {produto.Nome}
          </a>
          <p className="descricao__produto">{produto.Descricao}</p>
          <p className="valor__produto color">R${produto.Preco.toFixed(2)}</p>
          <button
            className="adicionar__carrinho"
            onClick={() => handleAdicionarCarrinho(produto)}
          >
            Adicionar ao Carrinho
          </button>
        </div>
      </div>
    </SwiperSlide>
  );

  return (
    <div>
      <main className="principal">
        {/* Novidades */}
        <section className="background__branco carrossel__full">
          <h2 className="principal__novidades__titulo">
            <b>Novidades</b>
          </h2>
          <Swiper
            spaceBetween={25}
            slidesPerView={window.innerWidth <= 1024 ? 2 : 3}
            centeredSlides
            loop
            navigation
            pagination={{ clickable: true }}
          >
            {novidades.map(renderProduto)}
          </Swiper>
        </section>

        {/* Mais Vendidos */}
        <section className="background__branco carrossel__full">
          <h2 className="principal__novidades__titulo">
            <b>Mais Vendidos</b>
          </h2>
          <Swiper
            spaceBetween={25}
            slidesPerView={window.innerWidth <= 1024 ? 2 : 3}
            centeredSlides
            loop
            navigation
            pagination={{ clickable: true }}
          >
            {maisVendidos.map(renderProduto)}
          </Swiper>
        </section>
      </main>
    </div>
  );
}

export default Home;
