import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { CarrinhoContext } from '../context/CarrinhoContext'; // Importar o contexto do carrinho

const TodosProdutos = () => {
  const [produtos, setProdutos] = useState([]);
  const { adicionarAoCarrinho } = useContext(CarrinhoContext); // Função do contexto do carrinho

  useEffect(() => {
    axios
      .get('http://localhost:5000/produto')
      .then((response) => setProdutos(response.data))
      .catch((error) => console.error('Erro ao buscar os produtos:', error));
  }, []);

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="produtos">
          {produtos.map((produto) => (
            <div key={produto.IDproduto} className="produtos__1">
              <Link to={`/produto/${produto.IDproduto}`} className="produto__link">
                <img className="produto__img" src={produto.Imagens} alt={produto.Nome} />
                <div className="produto__info">
                  <h3 className="produto__titulo">{produto.Nome}</h3>
                  <p className="produto__descricao">{produto.Descricao}</p>
                  <p className="produto__valor">R${produto.Preco.toFixed(2)}</p>
                </div>
              </Link>
              <button
                className="adicionar__carrinho"
                onClick={() =>
                  adicionarAoCarrinho({
                    IDproduto: produto.IDproduto,
                    Nome: produto.Nome,
                    Descricao: produto.Descricao,
                    Preco: produto.Preco,
                    Imagens: produto.Imagens,
                  })
                }
              >
                Adicionar ao Carrinho
              </button>
            </div>
          ))}
        </section>
      </main>
    </div>
  );
};

export default TodosProdutos;
