import React, { useEffect } from 'react';

const SobreNos = () => {

  const scrollToSection = (id) => {
    const targetElement = document.getElementById(id);
    if (targetElement) {
      window.scrollTo({
        top: targetElement.offsetTop - window.innerWidth * 0.09,
        behavior: 'smooth',
      });
    }
  };

  useEffect(() => {
    document.title = 'Fram Artesanatos - Sobre-n贸s';
  }, []);

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="opcoes">
          <div className="opcao">
            <button
              className="sobre__contato opcao__sobre"
              onClick={() => scrollToSection('sobre_nos')}
            >
              <p className="titulo__botao">Sobre N贸s</p>
            </button>
            <button
              className="sobre__contato opcao__contato"
              onClick={() => scrollToSection('contato')}
            >
              <p className="titulo__botao">Contate-nos</p>
            </button>
          </div>
        </section>

        <section className="background__branco background__sobre">
          <section className="sobre info" id="sobre_nos">
            <h2 className="titulo">Sobre N贸s</h2>
            <p className="texto">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Provident
              porro, repellat inventore voluptas illum vitae voluptatem recusandae
              deleniti obcaecati! Beatae doloremque, commodi non voluptatum suscipit
              impedit sunt eaque accusamus ipsum. Lorem ipsum dolor sit amet
              consectetur adipisicing elit. Atque aliquam non est adipisci
              voluptates veniam molestiae placeat aut, omnis inventore id vero a
              veritatis iste impedit mollitia numquam, eveniet molestias.
            </p>
          </section>

          <section className="contato info" id="contato">
            <h2 className="titulo">Contate-nos</h2>
            <p className="texto">
              Lorem ipsum dolor sit amet consectetur adipisicing elit. Eius
              laboriosam eveniet cupiditate dolore fugiat? Nulla amet similique,
              molestias voluptates laborum doloremque at rem cum repellendus ratione
              assumenda dignissimos commodi necessitatibus.
            </p>

            <p className="contato__numero contatos">
              <a href="https://wa.me/5511942957858" target="_blank" rel="noopener noreferrer">+55 (11) 94295-7858</a>
            </p>
            <p className="contato__email contatos">
              <a href="mailto:igoroliveira0150@gmail.com" target="_blank" rel="noopener noreferrer">igoroliveira0150@gmail.com</a>
            </p>
          </section>
        </section>
      </main>
    </div>
  );
};

export default SobreNos;
