import React, { createContext, useState, useEffect } from 'react';

// Criação do contexto
export const CarrinhoContext = createContext();

// Componente provider para envolver a aplicação
export const CarrinhoProvider = ({ children }) => {
  const [carrinho, setCarrinho] = useState([]);

  // Carregar carrinho do localStorage ao iniciar
  useEffect(() => {
    const carrinhoSalvo = JSON.parse(localStorage.getItem('carrinho')) || [];
    setCarrinho(carrinhoSalvo);
  }, []);

  // Função para adicionar produto ao carrinho
  const adicionarAoCarrinho = (produto) => {
    setCarrinho((prevCarrinho) => {
      // Verifica se o produto já está no carrinho
      const produtoExiste = prevCarrinho.find((item) => item.IDproduto === produto.IDproduto);

      if (produtoExiste) {
        alert('Este produto já está no carrinho!');
        return prevCarrinho; // Retorna o carrinho atual sem alterações
      }

      // Caso contrário, adiciona o produto ao carrinho
      const novoCarrinho = [...prevCarrinho, produto];
      localStorage.setItem('carrinho', JSON.stringify(novoCarrinho));
      return novoCarrinho;
    });
  };

  // Função para remover produto do carrinho
  const removerDoCarrinho = (idProduto) => {
    setCarrinho((prevCarrinho) => {
      const novoCarrinho = prevCarrinho.filter((produto) => produto.IDproduto !== idProduto);
      localStorage.setItem('carrinho', JSON.stringify(novoCarrinho));
      return novoCarrinho;
    });
  };

  return (
    <CarrinhoContext.Provider value={{ carrinho, adicionarAoCarrinho, removerDoCarrinho }}>
      {children}
    </CarrinhoContext.Provider>
  );
};
