import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css'; // Mudança aqui

function Home() {
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/produto')
      .then(response => setProdutos(response.data))
      .catch(error => console.error('Erro ao buscar produtos:', error));
  }, []);

  return (
    <div>
      <main className="principal">
        <section className="principal__novo_produto">
          <h2 className="principal__novo_produto__titulo">
            Descubra o novo tesouro da nossa coleção: um artesanato feito com amor
            e cuidado, trazendo frescor e beleza para sua vida.
          </h2>
          <p className="principal__novo_produto__p">
            Ótimo presente para você e seus entes queridos.
          </p>
          <a href="todos_produtos.html" className="principal__novo_produto__link">Compre Agora</a>
        </section>

        <section className="background__branco carrossel__full">
          <section className="principal__novidades">
            <div>
              <h2>Novidades</h2>
              <Swiper
                spaceBetween={25}
                slidesPerView={window.innerWidth <= 1024 ? 2 : 3}
                centeredSlides
                loop
                pagination={{ clickable: true }}
                navigation
              >
                {produtos.map(produto => (
                  <SwiperSlide key={produto.IDproduto}>
                    <div>
                      <img src={produto.Imagens} alt={produto.Nome} />
                      <h3>{produto.Nome}</h3>
                      <p>{produto.Descricao}</p>
                      <p>R${produto.Preco.toFixed(2)}</p>
                    </div>
                  </SwiperSlide>
                ))}
              </Swiper>
            </div>
          </section>
        </section>
        {/* O resto do código permanece o mesmo */}
      </main>
    </div>
  );
}

export default Home;
