import React, { useContext, useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { CarrinhoContext } from '../CarrinhoContext';

function Produto() {
  const { id } = useParams();
  const [produto, setProduto] = useState(null);
  const { adicionarAoCarrinho } = useContext(CarrinhoContext); // Usando o contexto do carrinho

  useEffect(() => {
    if (id) {
      axios.get(`http://localhost:5000/produto/${id}`)
        .then(response => setProduto(response.data))
        .catch(error => console.error('Erro ao buscar produto:', error));
    }
  }, [id]);

  const handleAdicionarCarrinho = () => {
    if (produto) {
      adicionarAoCarrinho(produto);
    }
  };

  if (!produto) {
    return <div>Carregando...</div>;
  }

  return (
    <div>
      <main className="principal">
        <section className="produto">
          <div className="box__produto">
            <div className="center__foto">
              <img className="foto__produto__principal" src={produto.Imagens} alt={produto.Nome} />
            </div>
            <div>
              <h2 className="nome__produto__principal">{produto.Nome}</h2>
              <h3 className="valor__produto__principal">R${produto.Preco.toFixed(2)}</h3>
              <p className="descricao__produto__principal">{produto.Descricao}</p>
              <button onClick={handleAdicionarCarrinho} className="adicionar__carrinho__produto">
                Adicionar ao Carrinho
              </button>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}

export default Produto;
