import React, { useState, useEffect } from 'react';



const GerenciarProdutos = () => {
  const [formData, setFormData] = useState({
    NomeProduto: '',
    ValorProduto: '',
    DescricaoProduto: '',
    ImgProduto: '',
    idproduto: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const response = await fetch('http://localhost:3000/produto', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          NomeProduto: formData.NomeProduto,
          ValorProduto: formData.ValorProduto,
          DescricaoProduto: formData.DescricaoProduto,
          ImgProduto: formData.ImgProduto,
        }),
      });
      const data = await response.json();
      alert(data.message || 'Produto criado com sucesso!');
    } catch (error) {
      alert('Erro ao criar produto!');
    }
  };

  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:3000/produto/${formData.idproduto}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          NomeProduto: formData.NomeProduto,
          ValorProduto: formData.ValorProduto,
          DescricaoProduto: formData.DescricaoProduto,
          ImgProduto: formData.ImgProduto,
        }),
      });
      const data = await response.json();
      alert(data.message || 'Produto atualizado com sucesso!');
    } catch (error) {
      alert('Erro ao atualizar produto!');
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:3000/produto/${formData.idproduto}`, {
        method: 'DELETE',
      });
      const data = await response.json();
      alert(data.message || 'Produto excluído com sucesso!');
    } catch (error) {
      alert('Erro ao excluir produto!');
    }
  };

  return (
    <div>
      <main className="principal margin_fixed">
        <h2>Gerenciar Produtos</h2>

        <h2>Adicionar Produto</h2>
        <input name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
        <input name="ValorProduto" placeholder="Preço" onChange={handleInputChange} />
        <input name="DescricaoProduto" placeholder="Descrição" onChange={handleInputChange} />
        <input name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
        <button onClick={handleSave}>Salvar</button>

        <h2>Atualizar Produto</h2>
        <input name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
        <input name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
        <input name="ValorProduto" placeholder="Preço" onChange={handleInputChange} />
        <input name="DescricaoProduto" placeholder="Descrição" onChange={handleInputChange} />
        <input name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
        <button onClick={handleUpdate}>Atualizar</button>

        <h2>Excluir Produto</h2>
        <input name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
        <button onClick={handleDelete}>Apagar</button>
      </main>
    </div>
  );
};

export default GerenciarProdutos;
