import React, { createContext, useState, useEffect } from 'react';

// Criação do contexto
export const CarrinhoContext = createContext();

// Componente provider para envolver a aplicação
export const CarrinhoProvider = ({ children }) => {
  const [carrinho, setCarrinho] = useState([]);

  // Carregar carrinho do localStorage ao iniciar
  useEffect(() => {
    const carrinhoSalvo = JSON.parse(localStorage.getItem('carrinho')) || [];
    setCarrinho(carrinhoSalvo);
  }, []);

  // Função para adicionar produto ao carrinho
  const adicionarAoCarrinho = (produto) => {
    setCarrinho((prevCarrinho) => {
      // Verifica se o produto já está no carrinho
      const produtoExiste = prevCarrinho.find((item) => item.IDproduto === produto.IDproduto);

      if (produtoExiste) {
        alert('Este produto já está no carrinho!');
        return prevCarrinho; // Retorna o carrinho atual sem alterações
      }

      // Caso contrário, adiciona o produto ao carrinho
      const novoCarrinho = [...prevCarrinho, produto];
      localStorage.setItem('carrinho', JSON.stringify(novoCarrinho));
      return novoCarrinho;
    });
  };

  // Função para remover produto do carrinho
  const removerDoCarrinho = (idProduto) => {
    setCarrinho((prevCarrinho) => {
      const novoCarrinho = prevCarrinho.filter((produto) => produto.IDproduto !== idProduto);
      localStorage.setItem('carrinho', JSON.stringify(novoCarrinho));
      return novoCarrinho;
    });
  };

  const produtoJaNoCarrinho = (idProduto) => {
    return carrinho.some((produto) => produto.IDproduto === idProduto);
  };

  const finalizarCompra = () => {
    if (carrinho.length === 0) {
      alert("Seu carrinho está vazio. Adicione produtos antes de finalizar a compra.");
      return;
    }

    const produtosValidos = carrinho.filter(produto => produto.Nome && produto.Preco);

    if (produtosValidos.length === 0) {
      alert("Não há produtos válidos no carrinho.");
      return;
    }

    const frete = 10.00;
    let subtotal = 0;
    let mensagem = "*Resumo do Seu Pedido:*\n\n";

    produtosValidos.forEach((produto, index) => {
      const preco = produto.Preco ? produto.Preco.toFixed(2) : "0.00";
      mensagem += `${index + 1}.  *${produto.Nome}* - R$${preco}\n`;
      subtotal += parseFloat(preco);
    });

    const total = subtotal + frete;
    mensagem += `\n*Subtotal:* R$${subtotal.toFixed(2)}`;
    mensagem += `\n*Total (Produtos + Frete):* R$${total.toFixed(2)}`;
    mensagem += `\n\nObrigado por comprar conosco!`;

    const numeroWhatsApp = "5511942957858";  // Coloque o número correto
    const mensagemCodificada = encodeURIComponent(mensagem);

    window.open(`https://wa.me/${numeroWhatsApp}?text=${mensagemCodificada}`, "_blank");
  };

  return (
    <CarrinhoContext.Provider
      value={{
        carrinho,
        adicionarAoCarrinho,
        removerDoCarrinho,
        produtoJaNoCarrinho,
        finalizarCompra,  // Função adicionada para finalizar a compra
      }}
    >
      {children}
    </CarrinhoContext.Provider>
  );
};
