import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Cabecalho = () => {
  const [menuAberto, setMenuAberto] = useState(false);

  const alternarMenu = () => {
    setMenuAberto(!menuAberto);
  };

  const fecharMenu = () => {
    setMenuAberto(false);
  };

  return (
    <header className="cabecalho">
      <nav className={`cabecalho__nav ${menuAberto ? 'ativo' : ''}`}>
        <button
          className="cabecalho__nav__menu_hamburguer"
          onClick={alternarMenu}
        >
          <div className={`menu-mobile ${menuAberto ? 'ativo' : ''}`}>
            <div className="linha1"></div>
            <div className="linha2"></div>
            <div className="linha3"></div>
          </div>
        </button>

        <ul className={`lista-nav ${menuAberto ? 'ativo' : ''}`}>
          <h2 className="lista-nav__titulo">Menu</h2>
          <li className="lista-nav__item">
            <Link to="/todosProdutos" onClick={fecharMenu}>
              <p className="fonte">Todos os Produtos</p>
            </Link>
          </li>

          <li className="lista-nav__item margin__baixo">
            <Link to="/sobre-nos" onClick={fecharMenu}>
              <p className="fonte">Sobre NÃ³s</p>
            </Link>
          </li>
        </ul>

        <h1 className="cabecalho__nav__titulo">
          <Link to="/" onClick={fecharMenu}>
            <b className="titulo_destaque">Fram</b> Artesanatos
          </Link>
        </h1>

        <section className="botao__carrinho">
          <Link to="/carrinho" className="botoes__carrinho" onClick={fecharMenu}>
            <img
              className="cabecalho__nav__carrinho_img"
              src="/images/global/icon_carrinho.svg"
              alt="Carrinho de compras"
            />
          </Link>
        </section>
      </nav>
    </header>
  );
};

export default Cabecalho;
