import React, { useContext } from 'react';
import { CarrinhoContext } from '../context/CarrinhoContext.js';
import { Link } from 'react-router-dom';

const Carrinho = () => {
  useEffect(() => {
    document.title = 'Fram Artesanatos - Home';
  }, []);
  
  const { carrinho, removerDoCarrinho, finalizarCompra } = useContext(CarrinhoContext);

  const handleRemoverCarrinho = (idProduto) => {
    removerDoCarrinho(idProduto);
  };

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="carrinho">
          {carrinho.length === 0 ? (
            <p className="titulo__carrinho titulo__novidades vazio">Seu carrinho est√° vazio!</p>
          ) : (
            <>
              <h2 className="titulo__carrinho titulo__novidades vazio">Seus Produtos</h2>
              {carrinho.map(produto => (
                <div key={produto.IDproduto} className="produto__carrinho">
                  <Link to={`/produto/${produto.IDproduto}`} className="produto__link">
                    <img className="produto__img" src={produto.Imagens} alt={produto.Nome} />
                    <div className="produto__info">
                      <h3 className="produto__titulo">{produto.Nome}</h3>
                      <p className="produto__descricao">{produto.Descricao}</p>
                      <p className="produto__valor">R${produto.Preco.toFixed(2)}</p>
                    </div>
                  </Link>
                  <button onClick={() => handleRemoverCarrinho(produto.IDproduto)} className="remover__carrinho adicionar__carrinho">
                    Remover
                  </button>
                </div>
              ))}
              <button
                className="comprar"
                onClick={finalizarCompra}
              >
                Finalizar Compra
              </button>
            </>
          )}
        </section>
      </main>
    </div>
  );
};

export default Carrinho;
