// Adicionar na parte do CarrinhoContext.js
const finalizarCompra = () => {
  if (carrinho.length === 0) {
    alert("Seu carrinho está vazio. Adicione produtos antes de finalizar a compra.");
    return;
  }

  const produtosValidos = carrinho.filter(produto => produto.Nome && produto.Preco);

  if (produtosValidos.length === 0) {
    alert("Não há produtos válidos no carrinho.");
    return;
  }

  const frete = 10.00;
  let subtotal = 0;
  let mensagem = "*Resumo do Seu Pedido:*\n\n";

  produtosValidos.forEach((produto, index) => {
    const preco = produto.Preco ? produto.Preco.toFixed(2) : "0.00";
    mensagem += `${index + 1}.  *${produto.Nome}* - R$${preco}\n`;
    subtotal += parseFloat(preco);
  });

  const total = subtotal + frete;
  mensagem += `\n*Subtotal:* R$${subtotal.toFixed(2)}`;
  mensagem += `\n*Total (Produtos + Frete):* R$${total.toFixed(2)}`;
  mensagem += `\n\nObrigado por comprar conosco!`;

  const numeroWhatsApp = "5511942957858";  // Coloque o número correto
  const mensagemCodificada = encodeURIComponent(mensagem);

  window.open(`https://wa.me/${numeroWhatsApp}?text=${mensagemCodificada}`, "_blank");
};
