import React, { useState, useEffect } from 'react';

const GerenciarProdutos = () => {

  useEffect(() => {
    document.title = 'Fram Artesanatos - Gerenciamento de Produtos';
  }, []);

  const [formData, setFormData] = useState({
    NomeProduto: '',
    ValorProduto: '',
    DescricaoProduto: '',
    ImgProduto: '',
    idproduto: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    try {
      const response = await fetch('http://localhost:3000/produto', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          NomeProduto: formData.NomeProduto,
          ValorProduto: formData.ValorProduto,
          DescricaoProduto: formData.DescricaoProduto,
          ImgProduto: formData.ImgProduto,
        }),
      });
      const data = await response.json();
      alert(data.message || 'Produto criado com sucesso!');
    } catch (error) {
      alert('Erro ao criar produto!');
    }
  };

  const handleUpdate = async () => {
    try {
      const response = await fetch(`http://localhost:3000/produto/${formData.idproduto}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          NomeProduto: formData.NomeProduto,
          ValorProduto: formData.ValorProduto,
          DescricaoProduto: formData.DescricaoProduto,
          ImgProduto: formData.ImgProduto,
        }),
      });
      const data = await response.json();
      alert(data.message || 'Produto atualizado com sucesso!');
    } catch (error) {
      alert('Erro ao atualizar produto!');
    }
  };

  const handleDelete = async () => {
    try {
      const response = await fetch(`http://localhost:3000/produto/${formData.idproduto}`, {
        method: 'DELETE',
      });
      const data = await response.json();
      alert(data.message || 'Produto excluÃ­do com sucesso!');
    } catch (error) {
      alert('Erro ao excluir produto!');
    }
  };

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="background__branco centralizado_gerenciar">
          <h2 className='titulo__carrinho titulo__novidades'>Gerenciar Produtos</h2>

          <div className='login__form_gerenciar'>
            <h2 cla>Adicionar Produto</h2>
            <input className='login__input_gerenciar' name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="ValorProduto" placeholder="PreÃ§o" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="DescricaoProduto" placeholder="DescriÃ§Ã£o" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
            <button className='button_gerenciar' onClick={handleSave}>Salvar</button>

            <h2 cla>Atualizar Produto</h2>
            <input className='login__input_gerenciar' name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="ValorProduto" placeholder="PreÃ§o" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="DescricaoProduto" placeholder="DescriÃ§Ã£o" onChange={handleInputChange} />
            <input className='login__input_gerenciar' name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
            <button className='button_gerenciar' onClick={handleUpdate}>Atualizar</button>

            <h2 cla>Excluir Produto</h2>
            <input className='login__input_gerenciar' name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
            <button className='button_gerenciar' onClick={handleDelete}>Apagar</button>
          </div>
        </section>
      </main>
    </div>
  );
};

export default GerenciarProdutos;
