import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation, Pagination } from 'swiper/modules';

import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';

function Home() {
  const [produtos, setProdutos] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/produto')
      .then((response) => setProdutos(response.data))
      .catch((error) => console.error('Erro ao buscar produtos:', error));
  }, []);

  return (
    <div>
      <main className="principal margin_fixed">
        <section className="produto">
          <div className="box__produto">
            <div className="center__foto">
              <img className="foto__produto__principal" src="" alt="Foto do Produto" />
            </div>
            <div>
              <h2 className="nome__produto__principal">Nome do Produto</h2>
              <h3 className="valor__produto__principal">R$00.00</h3>
              <p className="descricao__produto__principal">
                Descrição Produto
              </p>
              <button className="adicionar__carrinho__produto">
                Adicionar ao Carrinho
              </button>
            </div>
          </div>
        </section>

        <section className="background__branco carrossel__full">
          <h2 className="principal__novidades__titulo">
            <img src="assets/images/global/flor1.svg" /><b className="titulo__novidades">Mais Vendidos</b><img
              src="assets/images/global/flor2.svg" />
          </h2>
          <p className="principal__novidades__subtitulo">Veja Tudo</p>

          <article className="principal__novidades__carrossel">
            <div className="carrossel__container">
              <div className="swiper mais__vendidos">
                <div className="swiper-button-prev mais__vendidos">&lt;</div>
                <div className="swiper-button-next mais__vendidos">&gt;</div>
                <div className="swiper-pagination mais__vendidos"></div>
                <div className="swiper-wrapper mais__vendidos"></div>
              </div>
            </div>
          </article>
        </section>
      </main>
    </div>
  );
};

export default Carrinho;
