// src/pages/GerenciarProdutos.js
import React, { useState } from 'react';

const GerenciarProdutos = () => {
  const [formData, setFormData] = useState({
    NomeProduto: '',
    ValorProduto: '',
    DescricaoProduto: '',
    ImgProduto: '',
    idproduto: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSave = async () => {
    // Implementar chamada para criar um produto
  };

  const handleUpdate = async () => {
    // Implementar chamada para atualizar um produto
  };

  const handleDelete = async () => {
    // Implementar chamada para excluir um produto
  };

  return (
    <div>
      <h1>Gerenciar Produtos</h1>

      <h2>Adicionar Produto</h2>
      <input name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
      <input name="ValorProduto" placeholder="Preço" onChange={handleInputChange} />
      <input name="DescricaoProduto" placeholder="Descrição" onChange={handleInputChange} />
      <input name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
      <button onClick={handleSave}>Salvar</button>

      <h2>Atualizar Produto</h2>
      <input name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
      <input name="NomeProduto" placeholder="Nome" onChange={handleInputChange} />
      <input name="ValorProduto" placeholder="Preço" onChange={handleInputChange} />
      <input name="DescricaoProduto" placeholder="Descrição" onChange={handleInputChange} />
      <input name="ImgProduto" placeholder="URL da Imagem" onChange={handleInputChange} />
      <button onClick={handleUpdate}>Atualizar</button>

      <h2>Excluir Produto</h2>
      <input name="idproduto" placeholder="ID do Produto" onChange={handleInputChange} />
      <button onClick={handleDelete}>Apagar</button>
    </div>
  );
};

export default GerenciarProdutos;
